sys_element_mapping.value:
(function execute(inputs, outputs) {
  var delimitedString = inputs.delimitedString.toString();
  var delimiter = inputs.delimiter.toString();
  var array = delimitedString.split(delimiter);
  var kvpArray = [];

  if(!gs.nil(delimitedString)){
  for(var i = 0; i < array.length; i++){
    kvpArray.push('\\"' + array[i].toString() + '\\":\\"{{' + array[i].toString().toLowerCase().replace(' ','_') + '.value}}\\"');
  }
  outputs.kvpstring = kvpArray.join(',');
  }
  else {
    outputs.kvpstring = '';
  }
})(inputs, outputs);

