sys_variable_value.value:
(function execute(inputs, outputs) {
  var fArray = inputs.fieldArray;
  if(gs.nil(fArray[0].Field_Name_No_Spaces_)){
    fArray[0]=inputs.field;
  }
  else{
    fArray.push(inputs.field);
  }
  outputs.fieldarray = fArray;
})(inputs, outputs);

