sys_script_include.script:
var AdaptiveCardAjax = Class.create();
AdaptiveCardAjax.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    saveCard: function() {
        var cardName = this.getParameter('sysparm_card_name');
        var cardData = JSON.parse(this.getParameter('sysparm_card_data'));

        var utils = new AdaptiveCardGeneratorUtils();
        var cardSysId = utils.saveCard(cardName, cardData);

        return JSON.stringify({
            status: 'success',
            sys_id: cardSysId
        });
    },

    loadCard: function() {
        var cardSysId = this.getParameter('sysparm_sys_id');

        var utils = new AdaptiveCardGeneratorUtils();
        var cardData = utils.loadCard(cardSysId);

        if (cardData) {
            return JSON.stringify({
                status: 'success',
                card: cardData
            });
        } else {
            return JSON.stringify({
                status: 'error',
                message: 'Card not found'
            });
        }
    },

    type: 'AdaptiveCardAjax'
});
